void Pixmaps::createPixmaps()
{
	if (pixmapsCreated)
		return;

	QColor aColor = KDecoration::options()->color(KDecoration::ColorTitleBar, true);
	QColor iColor = KDecoration::options()->color(KDecoration::ColorTitleBar, false);

	QColor aFont = KDecoration::options()->color(KDecoration::ColorFont, true);
	QColor iFont = KDecoration::options()->color(KDecoration::ColorFont, false);

	// int titleSize = 18; // + 7 + 5  (top & mid spacers)
	// int borderSize = 9;
	//int buttonSize = 18;

	QPainter painter;
	KPixmap tempPixmap;

	// Let's create the button background.
	active_button_ground = new QPixmap(18, 18);
	painter.begin(active_button_ground);
	tempPixmap.resize(18, 18);
	tempPixmap.fill(aColor);
	painter.drawPixmap(0, 0, tempPixmap);
	painter.end();
	inactive_button_ground = new QPixmap(18, 18);
	painter.begin(inactive_button_ground);
	tempPixmap.resize(18, 18);
	tempPixmap.fill(iColor);
	painter.drawPixmap(0, 0, tempPixmap);
	painter.end();

	// Now, let's create the button icons!
	QImage *aGround = new QImage(18, 18, 32);
	QImage *taGround = new QImage();
	*taGround = active_button_ground->convertToImage();
	*taGround = taGround->convertDepth(32);
	bitBlt(aGround, 0, 0, taGround, 0, 0, 18, 18, 0);
	QImage *iGround = new QImage(18, 18, 32);
	QImage *tiGround = new QImage();
	*tiGround = inactive_button_ground->convertToImage();
	*tiGround = tiGround->convertDepth(32);
	bitBlt(iGround, 0, 0, tiGround, 0, 0, 18, 18, 0);

	int inverted = PowderHandler::hoverStyle();	// Regular (0) or Inverted (1)

	// Lotsa work to do.
	// Zero...
	static QImage* active_normal[12];
	static QImage* active_hover[12];
	static QImage* active_sunken[12];
	static QImage* inactive_normal[12];
	static QImage* inactive_hover[12];
	static QImage* inactive_sunken[12];
	// Sixty!
	static int* alias_pix[] = {
		alias_help, alias_max, alias_restore, alias_min,
		alias_close, alias_sticky, alias_unsticky, alias_above, 
		alias_unabove, alias_below, alias_unbelow, alias_menu};
	static int* glow_pix[] = {
		glow_help, glow_max, glow_restore, glow_min,
		glow_close, glow_sticky, glow_unsticky, glow_above, 
		glow_unabove, glow_below, glow_unbelow, glow_menu};
	int index[] = {
		PowderHandler::helpGlow(), PowderHandler::maxGlow(), 
		PowderHandler::maxGlow(), PowderHandler::minGlow(),
		PowderHandler::closeGlow(), PowderHandler::stickyGlow(),
		PowderHandler::stickyGlow(), PowderHandler::abbeGlow(),
		PowderHandler::abbeGlow(), PowderHandler::abbeGlow(),
		PowderHandler::abbeGlow(), PowderHandler::menuGlow()};
	// Some colors to use.
	int atr, atg, atb;		// Active title text color
	int itr, itg, itb;		// Inactive title text color
	aFont.getRgb(&atr, &atg, &atb);
	iFont.getRgb(&itr, &itg, &itb);
	int glr, glg, glb;		// Glow color
	QColor glow;
	// Zero...
	for (int b=0 ; b<12; b++)
	{
		glow = PowderHandler::listColor(index[b]);
		glow.getRgb(&glr, &glg, &glb);
		active_normal[b] = new QImage(18, 18, 32);
		bitBlt(active_normal[b], 0, 0, aGround, 0, 0, 18, 18, 0);
		unicity(alias_pix[b], *active_normal[b], atr, atg, atb);
		active_hover[b] = new QImage(18, 18, 32);
		bitBlt(active_hover[b], 0, 0, aGround, 0, 0, 18, 18, 0);
		active_sunken[b] = new QImage(18, 18, 32);
		bitBlt(active_sunken[b], 0, 0, aGround, 0, 0, 18, 18, 0);
		if (inverted==0)
		{
			unicity(glow_pix[b], *active_hover[b], glr, glg, glb);
			unicity(alias_pix[b], *active_hover[b], atr, atg, atb);
			unicity(glow_pix[b], *active_sunken[b], atr, atg, atb);
			unicity(alias_pix[b], *active_sunken[b], glr, glg, glb);
		}
		else
		{
			unicity(glow_pix[b], *active_hover[b], atr, atg, atb);
			unicity(alias_pix[b], *active_hover[b], glr, glg, glb);
			unicity(glow_pix[b], *active_sunken[b], glr, glg, glb);
			unicity(alias_pix[b], *active_sunken[b], atr, atg, atb);
		}
		inactive_normal[b] = new QImage(18, 18, 32);
		bitBlt(inactive_normal[b], 0, 0, iGround, 0, 0, 18, 18, 0);
		unicity(alias_pix[b], *inactive_normal[b], itr, itg, itb);
		inactive_hover[b] = new QImage(18, 18, 32);
		bitBlt(inactive_hover[b], 0, 0, iGround, 0, 0, 18, 18, 0);
		inactive_sunken[b] = new QImage(18, 18, 32);
		bitBlt(inactive_sunken[b], 0, 0, iGround, 0, 0, 18, 18, 0);
		if (inverted==0)
		{
			unicity(glow_pix[b], *inactive_hover[b], glr, glg, glb);
			unicity(alias_pix[b], *inactive_hover[b], atr, atg, atb);
			unicity(glow_pix[b], *inactive_sunken[b], atr, atg, atb);
			unicity(alias_pix[b], *inactive_sunken[b], glr, glg, glb);
		}
		else
		{
			unicity(glow_pix[b], *inactive_hover[b], atr, atg, atb);
			unicity(alias_pix[b], *inactive_hover[b], glr, glg, glb);
			unicity(glow_pix[b], *inactive_sunken[b], glr, glg, glb);
			unicity(alias_pix[b], *inactive_sunken[b], atr, atg, atb);
		}
	}
	// Sixty... This is the end.
	// Well, almost!
	// Zero...
	active_help_normal = active_normal[0];
	active_max_normal = active_normal[1];
	active_restore_normal = active_normal[2];
	active_min_normal = active_normal[3];
	active_close_normal = active_normal[4];
	active_sticky_normal = active_normal[5];
	active_unsticky_normal = active_normal[6];
	active_above_normal = active_normal[7];
	active_unabove_normal = active_normal[8];
	active_below_normal = active_normal[9];
	active_unbelow_normal = active_normal[10];
	active_menu_normal = active_normal[11];
	// Ten...
	active_help_hover = active_hover[0];
	active_max_hover = active_hover[1];
	active_restore_hover = active_hover[2];
	active_min_hover = active_hover[3];
	active_close_hover = active_hover[4];
	active_sticky_hover = active_hover[5];
	active_unsticky_hover = active_hover[6];
	active_above_hover = active_hover[7];
	active_unabove_hover = active_hover[8];
	active_below_hover = active_hover[9];
	active_unbelow_hover = active_hover[10];
	active_menu_hover = active_hover[11];
	// Twenty...
	active_help_sunken = active_sunken[0];
	active_max_sunken = active_sunken[1];
	active_restore_sunken = active_sunken[2];
	active_min_sunken = active_sunken[3];
	active_close_sunken = active_sunken[4];
	active_sticky_sunken = active_sunken[5];
	active_unsticky_sunken = active_sunken[6];
	active_above_sunken = active_sunken[7];
	active_unabove_sunken = active_sunken[8];
	active_below_sunken = active_sunken[9];
	active_unbelow_sunken = active_sunken[10];
	active_menu_sunken = active_sunken[11];
	// Thirty...
	inactive_help_normal = inactive_normal[0];
	inactive_max_normal = inactive_normal[1];
	inactive_restore_normal = inactive_normal[2];
	inactive_min_normal = inactive_normal[3];
	inactive_close_normal = inactive_normal[4];
	inactive_sticky_normal = inactive_normal[5];
	inactive_unsticky_normal = inactive_normal[6];
	inactive_above_normal = inactive_normal[7];
	inactive_unabove_normal = inactive_normal[8];
	inactive_below_normal = inactive_normal[9];
	inactive_unbelow_normal = inactive_normal[10];
	inactive_menu_normal = inactive_normal[11];
	// Forty...
	inactive_help_hover = inactive_hover[0];
	inactive_max_hover = inactive_hover[1];
	inactive_restore_hover = inactive_hover[2];
	inactive_min_hover = inactive_hover[3];
	inactive_close_hover = inactive_hover[4];
	inactive_sticky_hover = inactive_hover[5];
	inactive_unsticky_hover = inactive_hover[6];
	inactive_above_hover = inactive_hover[7];
	inactive_unabove_hover = inactive_hover[8];
	inactive_below_hover = inactive_hover[9];
	inactive_unbelow_hover = inactive_hover[10];
	inactive_menu_hover = inactive_hover[11];
	// Fifty...
	inactive_help_sunken = inactive_sunken[0];
	inactive_max_sunken = inactive_sunken[1];
	inactive_restore_sunken = inactive_sunken[2];
	inactive_min_sunken = inactive_sunken[3];
	inactive_close_sunken = inactive_sunken[4];
	inactive_sticky_sunken = inactive_sunken[5];
	inactive_unsticky_sunken = inactive_sunken[6];
	inactive_above_sunken = inactive_sunken[7];
	inactive_unabove_sunken = inactive_sunken[8];
	inactive_below_sunken = inactive_sunken[9];
	inactive_unbelow_sunken = inactive_sunken[10];
	inactive_menu_sunken = inactive_sunken[11];
	// Sixty. We're done!
}

