void Pixmaps::unicity(int* pattern, QImage& work, int rr, int gg, int bb)
{
	int x, y, aa;
	int z = 0;
	uint pxl;

	for (y=0; y<18; y++)
	{
		uint *line = (uint*)work.scanLine(y);	// work is 32-bit QImage.
		for (x=0; x<18; x++)
		{
			aa = pattern[z];	// Pattern is (18*18) * 256 levels
			//
			if (aa==0)		// Keep background.
			{
			}
			else if (aa==255)	// Replace background with color.
			{
				line[x] = qRgb(rr, gg, bb);
			}
			else			// Otherwise mix.
			{
				pxl = line[x];
				line[x] = qRgb( (qRed(pxl)*(255-aa)+rr*aa)/255, 
						(qGreen(pxl)*(255-aa)+gg*aa)/255, 
						(qBlue(pxl)*(255-aa)+bb*aa)/255 );
			}
			z++;
		}
	}
}
